package sanchez.jay.flujo;

public class Ejemplo_for {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i= 0; i < 10; i++) {
			for(int j=0; j<10; j++) {
				System.out.println("Hola alumnos");
			}
			//break; // Si quieres salir de cualquier bucle, debemos llamar la instruccion break, que en resumen, rompe con cualquier tipo de bucle
		}
		System.out.println("Haz salido del bucle");
	}
}
